import { authService } from "./auth-service"

const API_BASE = process.env.NEXT_PUBLIC_API_URL || "http://localhost:8080/api"

interface Game {
  id: number
  name: string
}

interface DefaultGameListResponse {
  defaultGames: Game[]
}

